import React, { Component } from 'react';

class PersonalCards extends Component{
    constructor(props){
        super(props);
        this.state = {pos : 'on'};
    }
    flipSwitch = () => {
        if( this.state.pos === "On" ) {
            this.setState({ pos: "Off" });
        } else {
            this.setState({ pos: "On" });
        }
    }
    render(){
        return(
            <>
                <h1>{this.props.lastName}, {this.props.firstName}</h1>
                <h3>Age: {this.props.age}</h3>
                <h3>Hair Color: {this.props.hairColor}</h3>
                <h3>{this.state.pos}</h3>
                <button onClick={ this.flipSwitch }>Birthday Button for {this.props.firstName} {this.props.lastName}</button>
            </>
        )
    }
}
export default PersonalCards;
